-- DROP table ITEM;
CREATE TABLE item(
    id INT PRIMARY KEY,
    name VARCHAR2(50) NOT NULL,
    category VARCHAR2(50) NOT NULL,
    price INT NOT NULL
);

-- DROP table ORDERS;
CREATE TABLE orders(
    id INT PRIMARY KEY,
    item_id INT REFERENCES item(id),
    quantity INT NOT NULL,
    order_date DATE NOT NULL
);

